import { useState } from 'react';
import './MegaSena.css';

const MegaSena = () => {
  const [numerosSorteados, setNumerosSorteado] = useState('Teste');

  const geraNumeros = () => {
    numerosSorteados = [];
    while (numerosSorteados.length < 6) {
      var numero = Math.floor(Math.random() * 60) + 1;
      if (numerosSorteados.indexOf(numero) === -1) {
        numerosSorteados.push(numero);
      }
    }

    return numerosSorteados;
  };

  console.log(numerosSorteados);

  return (
    <div>
      <h2>Mega-Sena</h2>
      <button onClick={geraNumeros}>Gerar n√∫meros</button>
      <p>{numerosSorteados}</p>
      <p>{setNumerosSorteado}</p>
    </div>
  );
};

export default MegaSena;
