  //       numerosSorteados.push(numero);
  //     }
  //   }

  //   return numerosSorteados;
  // };

  return (
    <div>
      <h2>Mega-Sena</h2>
      <button onClick={geraNumeros}>Gerar n√∫meros</button>
      <div>Numeros sorteados: {sorteio}</div>
    </div>
  );
};

export default MegaSena;
