import './MegaSena.css';

const MegaSena = () => {
  const geraNumeros = () => {
    const numeros = [];
    while (numeros.length < 6) {
      var numero = Math.floor(Math.random() * 60) + 1;
      if (numeros.indexOf(numero) === -1) {
        numeros.push(numero);
      }
    }
    console.log(numeros);
    return numeros;
  };

  return (
    <div>
      <h2>Mega-Sena</h2>
      <button onClick={geraNumeros}>Gerar n√∫meros</button>
      <p>{numeros}</p>
    </div>
  );
};

export default MegaSena;
