import { useState } from 'react';
import './MegaSena.css';

const MegaSena = () => {
  let [numerosSorteados, setNumerosSorteados] = useState('Teste');

  const geraNumeros = () => {
    setNumerosSorteados = [];
    while (setNumerosSorteados.length < 6) {
      var numero = Math.floor(Math.random() * 60) + 1;
      if (setNumerosSorteados.indexOf(numero) === -1) {
        setNumerosSorteados.push(numero);
      }
    }
    console.log(setNumerosSorteados);
    return setNumerosSorteados;
  };

  return (
    <div>
      <h2>Mega-Sena</h2>
      <button onClick={geraNumeros}>Gerar n√∫meros</button>
      {setNumerosSorteados.map((sorteados) => {
        <p>{sorteados}</p>;
      })}
    </div>
  );
};

export default MegaSena;
